import React, { Component } from "react";
import Node from "./Node/Node";

import "./PathfindingVisualizer.css";

export default class PathfindingVisualizer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nodes: [],
    };
  }

  componentDidMount() {
    const nodes = [];
    for (let row = 0; row < 9; row++) {
      const currentRow = [];
      for (let col = 0; col < 9; col++) {
        const currentNode = {
          col,
          row,
          value: 1,
          isStart: row === 0 && col === 0,
          isFinish: row === 8 && col === 8,
        };
        currentRow.push(currentNode);
      }
      nodes.push(currentRow);
    }
    this.setState({ nodes });
  }

  render() {
    const { nodes } = this.state;
    return (
      <div className = 'grid'>
        {nodes.map((row, rowIdx) => {
          return (
            <div key={rowIdx}>
              {row.map((node, nodeIdx) => {
                const { isStart, isFinish, value } = node;
                return (
                  <Node 
                    key = {nodeIdx}
                    isStart = {isStart}
                    isFinish={isFinish}
                    value={value}
                  ></Node>
                );
              })}
            </div>
          );
        })}
      </div>
    );
  }
}
