function Node(row, col) {
    return document.getElementById(`node-${row}-${col}`)
} // Gets a node from the screen

function NodeVal(row, col){
    return parseInt(document.getElementById(`node-${row}-${col}`).value)
}

function checkRow(row, value) {
    for (let col = 0; col < 9; col++) {
        if (NodeVal(row,col) === value) {
            return false
        }
    }
    return true
}

function checkCol(col, value) {
    for (let row = 0; row < 9; row++) {
        if (NodeVal(row,col) === value) {
            return false
        }
    }
    return true
}

function checkSquare(row, col,value) {
    let squareRow = Math.floor(row/3)*3
    let squareCol = Math.floor(col/3)*3

    for (let i = 0; i<3; i++) {
        for (let j = 0; j < 3; j++) {
            if (NodeVal(squareRow + i, squareCol + j) === value)
                return false
        }
    }
    return true
}

function checkValue(row, col, value) {
    if (checkRow(row, value) &&
        checkCol(col,value) &&
        checkSquare(row,col,value)) {
            return true
        }
        return false
    }

function nextEmptySpot() {
    for (var row = 0; row < 9; row++){
        for (var col = 0; col <9; col++){
            if (NodeVal(row, col) === 0) {
                return [row,col]
            }
        }
    }
    return [-1,-1]
}

function solve() {

    let emptySpot = nextEmptySpot()
    let row = emptySpot[0]
    let col = emptySpot[1]

    if (row === -1) {
        return
    }

    for (let num = 1; num <=9; num++) {
        if (checkValue(row,col,num)){
            Node(row,col).value = num
            this.forceUpdate()
            console.log('happy')
            setTimeout(function() {solve()},0.001)
            console.log('sad')
        }

    if(nextEmptySpot()[0] !== -1) {
        Node(row,col).value = 0
    }
    }
}

export default function backtracking(grid) {
    solve()
}
