function Node(row, col) {
  return document.getElementById(`node-${row}-${col}`)
} // Gets a node from the screen

export default function visualizeInitialAlg(moves) {
  //make initial border
  let tempMoves = moves[0].currMoves
  for (let i = 0; i < tempMoves.length; i++) {
    Node(tempMoves[i][0], tempMoves[i][1]).className = "node-finish"
  }
  //color corners
  tempMoves = moves[1].currMoves
  for (let i = 0; i < tempMoves.length; i++) {
    Node(tempMoves[i][0], tempMoves[i][1]).className = "node-visited"
  }

  helper(moves.slice(2))

  //show rest of moves
  function helper(moves) {
    setTimeout(() => {
      if (moves.length < 1) {
        return
      }

      //update total and waterLevel on screen
      document.getElementById(
        "Error"
      ).innerHTML = `Total Water: ${moves[0].total} \n Water Level: ${moves[0].waterLevel}`

      let move
      console.log(move)
      if (moves[0].currStep === 3) {
        for (let i = 0; i < moves[0].currMoves.length; i++) {
          //remove border node
          move = moves[0].currMoves[i]
          Node(move[0], move[1]).className = "node-visited"
        }
      } else if (moves[0].currStep === 2) {
        //highlight lowest border node
        move = moves[0].currMoves[0]
        Node(move[0], move[1]).className = "node-start"
        //remove highlight after 1 step
        setTimeout(() => {
          Node(move[0], move[1]).className = "node-finish"
        }, 1000)
      } else if (moves[0].currStep === 1) {
        //add new border node
        move = moves[0].currMoves[0]
        Node(move[0], move[1]).className = "node-finish"
      }

      helper(moves.slice(1))
    }, 500)
  }
}
