import React, { Component } from "react";
import Node from "./Node/Node";

import "./PathfindingVisualizer.css";

export default class PathfindingVisualizer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      grid: [],
    };
    this.visualizeAlgorithm = this.visualizeAlgorithm.bind(this)
  }

  componentDidMount() {
    const nodes = [];
    for (let row = 0; row < 9; row++) {
      const currentRow = [];
      for (let col = 0; col < 9; col++) {
        const currentNode = {
          col,
          row,
          actualValue: 0,
          isStart: row === 0 && col === 0,
          isFinish: row === 8 && col === 8,
        };
        currentRow.push(currentNode);
      }
      nodes.push(currentRow);
    }
    this.setState({
      grid: nodes
    });
  }

  visualizeAlgorithm(event) {
    console.print(event.target)
  }

  render() {
    const { nodes } = this.state;
    return (
      <>
        <select>
          <option>Please select an algorithm</option>
          <option>Algorithm 1</option> 
        </select>
        {/* <button onClick={() => this.visualizeAlgorithm()}>
          Visualize Dijkstra's Algorithm
        </button> */}
        <div className = 'grid'>
          {nodes.map((row, rowIdx) => {
            return (
              <div key={rowIdx}>
                {row.map((node, nodeIdx) => {
                  const { isStart, isFinish, actualValue } = node;
                  return (
                    <Node 
                      key = {nodeIdx}
                      isStart = {isStart}
                      isFinish={isFinish}
                      actualValue={actualValue}
                    ></Node>
                  );
                })}
              </div>
            );
          })}
        </div>
      </>);
  }
}
