function checkSquare(grid, row, col,value) {
    let squareRow = Math.floor(row/3)*3
    let squareCol = Math.floor(col/3)*3

    for (let i = 0; i<3; i++) {
        for (let j = 0; j < 3; j++) {
            if (grid[squareRow + i][squareCol + j] === value)
                return false
        }
    }
    return true
}

function fillInZeros(grid) {
    let nums= [1,2,3,4,5,6,7,8,9]
    let squareRow = 0
    let squareCol = 0

    for (let square = 0; square < 9; square++) {
        for (let row = squareRow; row<3+squareRow; row++) {
            for (let col = squareCol; col < 3+squareCol; col++) {
                if (grid[row][col] !== 0) {
                    nums[grid[row][col] - 1] = 0
                }
            }
        }
        removeElement(nums,0)
        shuffle(nums)
        for (let row = squareRow; row<3+squareRow; row++) {
            for (let col = squareCol; col < 3+squareCol; col++) {
                if (grid[row][col] === 0) {
                    grid[row][col] = nums.pop()
                }
            }
        }
        nums= [1,2,3,4,5,6,7,8,9]
    }
}


function solve(grid) {
    fillInZeros(grid)


}

export default function simulatedAnnealing () {
    const grid = [
        [0, 5, 1, 3, 6, 2, 7, 0, 0],
        [0, 4, 0, 0, 5, 8, 0, 0, 0],
        [0, 0, 0, 4, 0, 0, 0, 2, 5],
        [0, 8, 0, 0, 0, 0, 9, 0, 3],
