        [7, 0, 5, 0, 0, 0, 0, 8, 0],
        [1, 2, 0, 0, 0, 9, 0, 0, 0],
        [0, 0, 0, 2, 8, 0, 0, 6, 0],
        [0, 0, 8, 5, 3, 4, 2, 9, 0]
    ]

    let moves = []

    for (let row = 0; row < 9; row++){
        for (let col = 0; col <9; col++){
            Node(row,col).value = grid[row][col]
        }
    }

    solve(grid,moves)

    for (let row = 0; row < 9; row++){
        for (let col = 0; col <9; col++){
            Node(row,col).value = grid[row][col]
        }
    }

    return moves
}
