//FIXME this function doesn't work because moves is undefined. We could store moves in
//App after running initialAlg, but it may be easier to change visualizeAlgorithm to remove the timeouts
//for changing of nodes back and just change the color based off the previous iteration

function Node(row, col) {
  return document.getElementById(`node-${row}-${col}`)
} // Gets a node from the screen

//this function is bound to App
//so that it can call the functions in App

export default function visualizeInitialAlg(moves, solvable) {
  //copy funtions from app so they can be called within helper
  let setCurrIndex = this.setCurrIndex

  //Check if matrix is too small
  if (!solvable) {
    console.log("matrix too small")
    document.getElementById("Error").innerHTML =
      "Matrix has no inner tiles. Total Water = 0."
    return
  }

  let currIndex = this.state.currIndex //how far we are along moves
  console.log("In visualizeInitialAlg", currIndex)

  //reset className of <div id="end"><div> if it's still highlighted fromprevious run
  document.getElementById("end").className = ""

  if (currIndex < 1) {
    //make initial border
    let tempMoves = moves[0].currMoves
    for (let i = 0; i < tempMoves.length; i++) {
      Node(tempMoves[i][0], tempMoves[i][1]).className = "node-finish"
    }
    //color corners
    tempMoves = moves[1].currMoves
    for (let i = 0; i < tempMoves.length; i++) {
      Node(tempMoves[i][0], tempMoves[i][1]).className = "node-visited"
    }

    //set About color
    document.getElementById("step1").className = "currStep"

    //we finished step 0, so currIndex = 2 now
    currIndex = 2
    this.setState({ currIndex: currIndex })
    return
  } else {
    //curIndex >== 2

    //reset About color
    document.getElementById("step1").className = ""

    helper(moves.slice(currIndex))
    return
  }

  //show rest of moves
  function helper(moves) {
    if (moves.length < 1) {
      document.getElementById("end").className = "currStep"
      //update currIndex
      setCurrIndex(currIndex)
      return currIndex
    }

    //set About color
    document.getElementById(`step${moves[0].currStep + 1}`).className =
      "loopItem currStep"
    //reset About color
    setTimeout(() => {
      document.getElementById(`step${moves[0].currStep + 1}`).className =
        "loopItem"
    }, 500)

    //update total and waterLevel on screen
    document.getElementById(
      "Error"
    ).innerHTML = `Total Water: ${moves[0].total} \n Water Level: ${moves[0].waterLevel}`

    let move
    if (moves[0].currStep === 1) {
      //highlight lowest border node
      move = moves[0].currMoves[0]
      Node(move[0], move[1]).className = "node-start"
      //remove highlight after 1 step
      setTimeout(() => {
        Node(move[0], move[1]).className = "node-finish"
      }, 1000)
    } else if (moves[0].currStep === 2) {
      //add new border node
      move = moves[0].currMoves[0]
      Node(move[0], move[1]).className = "node-finish"
    } else if (moves[0].currStep === 3) {
      for (let i = 0; i < moves[0].currMoves.length; i++) {
        //remove border node
        move = moves[0].currMoves[i]
        Node(move[0], move[1]).className = "node-visited"
      }
    }

    //we finished another step so increment currIndex
    currIndex += 1
    setCurrIndex(currIndex)
  }
}

//FIXME
//there is a bug with the yellow tile i.e highlighting the lowest border cell.
//This highlighting turns off after a second or whatever so you need to find a way to stop this
//when you pause, otherwise the highlighted stile will turn back to pink
//also the highlighting of the steps on the left side of the screen has this problem too
