import React from "react";
import "./App.css";
import PathfindingVisualizer from "./PathfindingVisualizer/PathfindingVisualizer";
import Menu from './Menu'
import backtracking from './algorithms/backtracking'
import visualizeBacktracking from './algorithms/visualizeBacktracking.js'
import simulatedAnnealing from "./algorithms/simulatedAnnealing";
import visualizeSimulatedAnnealing from "./algorithms/visualizeSimulatedAnnealing";
import loadGrid from './loadGrid'
import loadSGrid from './loadSGrid'
import hillClimbing from "./algorithms/hillClimbing";
import About from './About'
import selectAlg from './selectAlg'
import visualizeAlgorithm from './visualizeAlgorithm'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      grid: [],
      selectedAlg: 0
    };
    this.visualizeAlgorithm = visualizeAlgorithm.bind(this)
    this.selectAlg = selectAlg.bind(this)
  }

  render() {
    return (
      <div className="App">
        <h1>Algorithm Visualizer</h1>
        <Menu
          selectAlg={this.selectAlg}
          props={this.state} 
          handleClick={this.visualizeAlgorithm} 
          loadSGrid={() => {
            this.setState({grid: loadSGrid()}, () => loadGrid(this.state.grid))}}
        ></Menu>
        <About selectedAlg={this.state.selectedAlg}></About>
        <PathfindingVisualizer></PathfindingVisualizer>
        {this.state.selectedAlg === 2 || this.state.selectedAlg === 3 ?
          <text>Error</text>
          : null
        }
        <h3>p.s. I love you :heart:</h3>
      </div>
    );
  }
}

export default App;
