function Node(row, col) {
    return document.getElementById(`node-${row}-${col}`)
} // Gets a node from the screen

export default function visualizeBacktracking(moves) {
    // let moves = [0,0,0]
    // moves.push(movesList)
    
    for (let i = 1; i < moves.length; i++) {
        // row = moves[i][0]
        // col = moves[i][1]
        // value = moves[i][2]
        // setTimeout(() => {
        //     Node(moves[i][0],moves[i][1]).className = 'node-start'
        // }, 5000*Math.pow(Math.log(i+1),0.5)-2000)
        // setTimeout(() => {
        //     Node(moves[i][0],moves[i][1]).value = moves[i][2]
        //     Node(moves[i][0],moves[i][1]).className = 'node-finish'
        // }, 5000*Math.pow(Math.log(i+2),0.5)-2000)
    
        setTimeout(() => {
            Node(moves[i][0],moves[i][1]).className = 'node-start' //color node about to be changed
        }, 5000*Math.pow(i-1,0.25)-1000)
        setTimeout(() => {
            Node(moves[i][0],moves[i][1]).value = moves[i][2]
            Node(moves[i][0],moves[i][1]).className = 'node-finish' //color visited nodes
            if (moves[i][0] <= moves[i-1][0]) {
                if (moves[i][1] <= moves[i-1][1]) {
                    Node(moves[i-1][0],moves[i-1][1]).className = 'node'
                    Node(moves[i-1][0],moves[i-1][1]).value = 0
                }
            }
        }, 5000*Math.pow(i,0.25)-1000)
    }
}
