import React from "react";
import "./App.css";
import PathfindingVisualizer from "./PathfindingVisualizer/PathfindingVisualizer";
import Menu from './Menu'
import loadGrid from './loadGrid'
import loadSGrid from './loadSGrid'
import About from './About'
import selectAlg from './selectAlg'
import visualizeAlgorithm from './visualizeAlgorithm'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      grid: [],
      selectedAlg: 0
    };
    this.visualizeAlgorithm = visualizeAlgorithm.bind(this)
    this.selectAlg = selectAlg.bind(this)
  }

  render() {
    return (
      <div className="App">
        <h1>Algorithm Visualizer</h1>
        <Menu
          selectAlg={this.selectAlg}
          props={this.state} 
          handleClick={this.visualizeAlgorithm} 
          loadSGrid={() => {
            this.setState({grid: loadSGrid()}, () => loadGrid(this.state.grid))}}
        ></Menu>
        <section className='flexbox'>
        <span><About className='About' className='About' selectedAlg={this.state.selectedAlg}></About></span>
         <span> <PathfindingVisualizer className='PathfindingVisualizer'></PathfindingVisualizer></span>
          {this.state.selectedAlg === 2 || this.state.selectedAlg === 3 ?
            <span className='Error'>Error</span>
            : null
          }
        </section>
        <h3>p.s. I love you :heart:</h3>
      </div>
    );
  }
}

export default App;
