import React from "react";
import "./App.css";
import PathfindingVisualizer from "./PathfindingVisualizer/PathfindingVisualizer";
import Menu from './Menu'
import loadGrid from './loadGrid'
import loadSGrid from './loadSGrid'
import About from './About'
import selectAlg from './selectAlg'
import visualizeAlgorithm from './visualizeAlgorithm'
import Error from './Error'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      grid: [],
      selectedAlg: 0
    };
    this.visualizeAlgorithm = visualizeAlgorithm.bind(this)
    this.selectAlg = selectAlg.bind(this)
  }

  componentDidMount() {
    const nodes = [];
    for (let row = 0; row < 9; row++) {
      const currentRow = [];
      for (let col = 0; col < 9; col++) {
        const currentNode = {
          col,
          row,
          actualValue: 0,
          isStart: false,
          isFinish: false,
        };
        currentRow.push(currentNode);
      }
      nodes.push(currentRow);
    }
    this.setState({
      grid: nodes
    });
  }

  render() {
    return (
      <div className="App">
        <h1>Algorithm Visualizer</h1>
        <Menu
          selectAlg={this.selectAlg}
          props={this.state} 
          handleClick={this.visualizeAlgorithm} 
          loadSGrid={() => {
            this.setState({grid: loadSGrid()}, () => loadGrid(this.state.grid))}}
        ></Menu>
        <div className='flexbox'>
          <About selectedAlg={this.state.selectedAlg}></About>
          <PathfindingVisualizer grid={this.state.grid} ></PathfindingVisualizer>
          {this.state.selectedAlg === 2 || this.state.selectedAlg === 3 ?
            <Error></Error>
            : <div></div>
          }
        </div>
        <h3>p.s. I love you :heart:</h3>
      </div>
    );
  }
}

export default App;
