function Node(row, col) {
    return document.getElementById(`node-${row}-${col}`)
} // Gets a node from the screen

function NodeVal(row, col){
    return Number(document.getElementById(`node-${row}-${col}`).value)
}

function checkRow(row, value) {
    for (let col = 0; col < 9; col++) {
        if (Node(row,col).value === value) {
            return false
        }
    }
    return true
}

function checkCol(col, value) {
    for (let row = 0; row < 9; row++) {
        if (Node(row,col).value === value) {
            return false
        }
    }
    return true
}

function checkSquare(row, col,value) {
    let squareRow = Math.flooor(row/3)*3
    let squareCol = Math.floor(col/3)*3

    for (let i = 0; i<3; i++) {
        for (let j = 0; j < 3; j++) {
            if (Node(squareRow + i, squareCol + j).value === value)
                return false
        }
    }
    return true
}

function solve() {
    for (let i =0; i< 4;i++) {
        console.log(checkCol(i, 0))
    }
}

export default function backtracking(grid) {
    solve()
}
