import initialAlg from "./algorithms/initialAlg"
import visualizeInitialAlg from "./algorithms/visualizeInitialAlg"
import visualizeStepBack from "./algorithms/visualizeStepBack"

//This function is bound to App.js so that App may
//call any algorithm and visualize it

export default function visualizeAlgorithm(changeNodeVal) {
  switch (this.state.selectedAlg) {
    case 0:
      console.log("No algorithm selected!")
      break

    case 1:
      if (this.state.mode === "play") {
        document.getElementById("playPause").innerHTML = "Pause"
        let temp = initialAlg(this.state.grid)
        visualizeInitialAlg(temp.moves, temp.solvable)
        break
      }
      //mode === "pause"
      document.getElementById("playPause").innerHTML = "Play"
      let temp = initialAlg(this.state.grid)
      visualizeStepBack(temp.moves) //we know it's solvable, so we don't need the variable solvable
      break

    default:
      console.log("You selected an algorithm!")
      break
  }
}
