import React, { Component } from "react";
import Node from "./Node/Node";

import "./PathfindingVisualizer.css";
import backtracking from '../algorithms/backtracking.js'

export default class PathfindingVisualizer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      grid: [],
      selectedAlg: 0
    };
    this.visualizeAlgorithm = this.visualizeAlgorithm.bind(this)
    this.changeAlgorithm = this.changeAlgorithm.bind(this)
    this.loadGrid = this.loadGrid.bind(this)
    this.Node = this.Node.bind(this)
  }

  componentDidMount() {
    const nodes = [];
    for (let row = 0; row < 9; row++) {
      const currentRow = [];
      for (let col = 0; col < 9; col++) {
        const currentNode = {
          col,
          row,
          actualValue: 0,
          isStart: row === 0 && col === 0,
          isFinish: row === 8 && col === 8,
        };
        currentRow.push(currentNode);
      }
      nodes.push(currentRow);
    }
    this.setState({
      grid: nodes
    });
  }

  Node(row, col) {
    return document.getElementById(`node-${row}-${col}`)
  } // Gets a node from the screen

  loadGrid() {
    document.getElementById(`node-${0}-${0}`).value = 5
  }

  visualizeAlgorithm() {
    switch(this.state.selectedAlg) {
      case 0:
        console.log("No algorithm selected!")
        break
      case 1:
        backtracking(this.state.grid)
        break
      default:
        console.log("You selected an algorithm!")
        break
    }
  }

  changeAlgorithm(event) {
    const {value} = event.target
    this.setState({
      selectedAlg: Number(value)
    }) // WHY IS THIS SENDING A STRING WTF
  }

  render() {
    const { grid } = this.state;
    return (
      <>
        <select
          value = {this.state.selectedAlg}
          onChange = {this.changeAlgorithm}
        >
          <option value={0}>Please select an algorithm</option>
          <option value={1}>Algorithm 1</option>
          <option value={2}>Algorithm 2</option>
        </select>
        <button onClick={() => this.visualizeAlgorithm()}>
          Visualize Algorithm
        </button>
        <button onClick={() => this.loadGrid()}>
          Load Grid
          </button>
        <div className = 'grid'>
          {grid.map((row, rowIdx) => {
            return (
              <div key={rowIdx}>
                {row.map((node, nodeIdx) => {
                  const { col, row, isStart, isFinish, actualValue } = node;
                  return (
                    <Node 
                      key = {nodeIdx}
                      col = {col}
                      row = {row}
                      isStart = {isStart}
                      isFinish={isFinish}
                      actualValue={actualValue}
                    ></Node>
                  );
                })}
              </div>
            );
          })}
        </div>
      </>);
  }
}
