function Node(row, col) {
    return document.getElementById(`node-${row}-${col}`)
} // Gets a node from the screen

function checkRow(grid, row, value) {
    for (let col = 0; col < 9; col++) {
        if (grid[row][col] === value) {
            return false
        }
    }
    return true
}

function checkCol(grid, col, value) {
    for (let row = 0; row < 9; row++) {
        if (grid[row][col] === value) {
            return false
        }
    }
    return true
}

function checkSquare(grid, row, col,value) {
    let squareRow = Math.floor(row/3)*3
    let squareCol = Math.floor(col/3)*3

    for (let i = 0; i<3; i++) {
        for (let j = 0; j < 3; j++) {
            if (grid[squareRow + i][squareCol + j] === value)
                return false
        }
    }
    return true
}

function checkValue(grid, row, col, value) {
    if (checkRow(grid, row, value) &&
        checkCol(grid, col,value) &&
        checkSquare(grid, row,col,value)) {
            return true
        }
        return false
    }

function nextEmptySpot(grid ) {
    for (var row = 0; row < 9; row++){
        for (var col = 0; col <9; col++){
            if (grid[row][col] === 0) {
                return [row,col]
            }
        }
    }
    return [-1,-1]
}

function solve(grid,moves) {
    let emptySpot = nextEmptySpot(grid)
    let row = emptySpot[0]
    let col = emptySpot[1]

    if (row === -1) {
        return
    }

    for (let num = 1; num <=9; num++) {
        if (checkValue(grid, row,col,num)){
            grid[row][col] = num
            moves.push([row,col,num])
            solve(grid,moves)
        }

    if(nextEmptySpot(grid)[0] !== -1) {
        grid[row][col] = 0
        // moves.push([row,col,0])
    }
    }
}

export default function backtracking() {
    const grid = [
        [0, 5, 1, 3, 6, 2, 7, 0, 0],
        [0, 4, 0, 0, 5, 8, 0, 0, 0],
        [0, 0, 0, 4, 0, 0, 0, 2, 5],
        [0, 8, 0, 0, 0, 0, 9, 0, 3],
        [0, 0, 0, 0, 0, 0, 0, 0, 0],
        [7, 0, 5, 0, 0, 0, 0, 8, 0],
        [1, 2, 0, 0, 0, 9, 0, 0, 0],
        [0, 0, 0, 2, 8, 0, 0, 6, 0],
        [0, 0, 8, 5, 3, 4, 2, 9, 0]
    ]

    let moves = []

    for (let row = 0; row < 9; row++){
        for (let col = 0; col <9; col++){
            Node(row,col).value = grid[row][col]
        }
    }

    solve(grid,moves)

    // for (let row = 0; row < 9; row++){
    //     for (let col = 0; col <9; col++){
    //         Node(row,col).value = grid[row][col]
    //     }
    // }

    return moves
}
