function fillInZeros(grid) {
    let nums= [1,2,3,4,5,6,7,8,9]
    let squareRow = 0
    let squareCol = 0

    for (let square = 0; square < 9; square++) {
        for (let row = squareRow; row<3+squareRow; row++) {
            for (let col = squareCol; col < 3+squareCol; col++) {
                if (grid[row][col] !== 0) {
                    nums[grid[row][col] - 1] = 0
                }
            }
        }
        removeElement(nums,0)
        shuffle(nums)
        for (let row = squareRow; row<3+squareRow; row++) {
            for (let col = squareCol; col < 3+squareCol; col++) {
                if (grid[row][col] === 0) {
                    grid[row][col] = nums.pop()
                }
            }
        }
        nums= [1,2,3,4,5,6,7,8,9]
        if (squareCol !== 6) {
            squareCol = squareCol + 3
        }
        else {
            squareCol = 0
            squareRow = squareRow + 3
        }
    }
}

function energy(grid) {
    let E = 0
    let seen = []

    //check the rows
    for (let row = 0; row < 9; row ++) {
        for (let col = 0; col < 9; col ++) {
            if (seen.includes(grid[row][col])) {
                E = E + 1
            }
            else {
                seen.push(grid[row][col])
            }
        }
        seen = []
    }
    //check the columns
    for (let col = 0; col < 9; col ++) {
        for (let row = 0; row < 9; row ++) {
            if (seen.includes(grid[row][col])) {
                E = E + 1
            }
            else {
                seen.push(grid[row][col])
            }
        }
        seen = []
    }

    return E
}

function getRandomInt(max) {
    return Math.floor(Math.random() * Math.floor(max));
  }

function move (grid) {
    //pick a random square
    let squareRow = getRandomInt(3)
    let squareCol = getRandomInt(3)
    console.log(squareCol)

    //pick a random 2 nodes in that square that aren't the same
    let row1 = getRandomInt(3) + squareRow
    let col1 = getRandomInt(3) + squareCol

    let row2 = getRandomInt(3)+ squareRow
    let col2 = getRandomInt(3)+ squareCol

    while (row1 === row2 && col1 === col2) {
        row2 = getRandomInt(3)+ squareRow
        col2 = getRandomInt(3)+ squareCol
    }

    //swap their values
    let temp = grid[row1][row2]
    grid[row2][col2] = grid[row1][col1]
    grid[row1][col1] = temp
}

function solve(grid) {
    fillInZeros(grid)

    for (let row = 0; row < 9; row++){
        for (let col = 0; col <9; col++){
            Node(row,col).value = grid[row][col]
        }
    }

    setTimeout(move(grid),1000)

}

export default function simulatedAnnealing () {
    const grid = [
        [0, 5, 1, 3, 6, 2, 7, 0, 0],
        [0, 4, 0, 0, 5, 8, 0, 0, 0],
        [0, 0, 0, 4, 0, 0, 0, 2, 5],
