import React, { Component } from "react";
import Node from "./Node/Node";

import "./PathfindingVisualizer.css";
import backtracking from '../algorithms/backtracking.js'
import loadGrid from './loadGrid'
import visualizeBacktracking from '../algorithms/visualizeBacktracking.js'
import simulatedAnnealing from "../algorithms/simulatedAnnealing";

export default class PathfindingVisualizer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      grid: [],
      selectedAlg: 0
    };
    this.visualizeAlgorithm = this.visualizeAlgorithm.bind(this)
    this.changeAlgorithm = this.changeAlgorithm.bind(this)
  }

  componentDidMount() {
    const nodes = [];
    for (let row = 0; row < 9; row++) {
      const currentRow = [];
      for (let col = 0; col < 9; col++) {
        const currentNode = {
          col,
          row,
          actualValue: 0,
          isStart: false,
          isFinish: false,
        };
        currentRow.push(currentNode);
      }
      nodes.push(currentRow);
    }
    this.setState({
      grid: nodes
    });
  }

  visualizeAlgorithm() {
    switch(this.state.selectedAlg) {
      case 0:
        console.log("No algorithm selected!")
        break

      case 1:
        let moves = backtracking(this.state.grid)
        visualizeBacktracking(moves)
        break
      
      case 2:
        simulatedAnnealing()
        break
      
      default:
        console.log("You selected an algorithm!")
        break
    }
  }

  changeAlgorithm(event) {
    const {value} = event.target
    this.setState({
      selectedAlg: Number(value)
    }) // WHY IS THIS SENDING A STRING WTF
  }

  render() {
    const { grid } = this.state;
    return (
      <>
        <select
          value = {this.state.selectedAlg}
          onChange = {this.changeAlgorithm}
        >
          <option value={0}>Please select an algorithm</option>
          <option value={1}>Backtracking</option>
          <option value={2}>Simulated Annealing</option>
        </select>
        <button onClick={() => this.visualizeAlgorithm()}>
          Visualize Algorithm
        </button>
        <button onClick={() => loadGrid()}>
          Load Grid
          </button>
        <div className = 'grid'>
          {grid.map((row, rowIdx) => {
            return (
              <div key={rowIdx}>
                {row.map((node, nodeIdx) => {
                  const { col, row, isStart, isFinish, actualValue } = node;
                  return (
                    <Node 
                      key = {nodeIdx}
                      col = {col}
                      row = {row}
                      isStart = {isStart}
                      isFinish={isFinish}
                      actualValue={actualValue}
                    ></Node>
                  );
                })}
              </div>
            );
          })}
        </div>
      </>);
  }
}
