import React from "react";
import "./App.css";
import PathfindingVisualizer from "./PathfindingVisualizer/PathfindingVisualizer";
import Menu from './Menu'
import backtracking from './algorithms/backtracking'
import visualizeBacktracking from './algorithms/visualizeBacktracking.js'
import simulatedAnnealing from "./algorithms/simulatedAnnealing";
import visualizeSimulatedAnnealing from "./algorithms/visualizeSimulatedAnnealing";
import loadGrid from './loadGrid'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      grid: [],
      selectedAlg: 0
    };
    this.visualizeAlgorithm = this.visualizeAlgorithm.bind(this)
    this.changeAlgorithm = this.changeAlgorithm.bind(this)
  }

  visualizeAlgorithm() {
    let moves = []
    switch(this.state.selectedAlg) {
      case 0:
        console.log("No algorithm selected!")
        break

      case 1:
        moves = backtracking(this.state.grid)
        visualizeBacktracking(moves)
        break

      case 2:
        moves = simulatedAnnealing(this.state.grid)
        setTimeout(visualizeSimulatedAnnealing(moves),1000)
        break

      default:
        console.log("You selected an algorithm!")
        break
    }
  }

  changeAlgorithm(event) {
    const {value} = event.target
    this.setState({
      selectedAlg: Number(value)
    }) // WHY IS THIS SENDING A STRING WTF
  }

  render() {
    return (
      <div className="App">
        <h1>Algorithm Visualizer</h1>
        <Menu props={this.state} handleClick={this.visualizeAlgorithm} loadGrd={loadGrid}></Menu>
        <PathfindingVisualizer></PathfindingVisualizer>
        <h3>p.s. I love you :heart:</h3>
      </div>
    );
  }
}

export default App;
